mapper.update:
update confirm_email
set
email_auth = true
where (select mem_id from mem_info where mem_email = #{mem_email}) = #{mem_id}
and email_key = #{authCode};

mapper.select:
select count(*)
from mem_info
where mem_id = #{mem_id}

select count(*)
from mem_info
where mem_email = #{mem_email}

select count(*)
from mem_info
where mem_nick = #{mem_nick}

mapper.update:
update mem_info
set
mem_email = #{mem_email},
mem_nick = #{mem_nick}
where mem_id = #{mem_id}

update mem_info
set
mem_pw = #{new_pw}
where mem_id = #{mem_id}

mapper.select:
select mem_auth
from mem_option
where mem_id = #{mem_id}

select mem_post_def_secret
from mem_option
where mem_id = #{mem_id}
